import React from 'react';



class Navbar extends React.Component {



render() {
  $(() => {
    // Add scroll spy to the body
    $("body")
        .attr("data-spy", "scroll")
        .attr("data-target", ".navbar")
        .attr("data-offset", 54);
    
    //Mark as active the clicked nav button
    $(".navbar-nav li").on("click", e => {
        $(e.target).addClass("active");
    });
    
    // Add bigger class to social icons when hovered and remove when not focused
    $(".fa-lg").hover(
        () => $(".fa-lg:hover").addClass("fa-2x"),
        () => $(".fa-lg:not(:hover)").removeClass("fa-2x")
    );

    // Add filter and icon top of the portfolio item
    $(".portfolio-item").hover(
        // handlerIn
        e => {
            $(e.target).css("filter", "opacity(.55)");
            let icon =`<i
                id="portfolio-item-hovered-icon"
                class="fa fa-binoculars fa-2x"
                aria-hidden="true" 
                style="
                    position: absolute;
                    z-index: 100;
                    bottom: 50%;
                    right: 40%;
                    text-decoration: none;
                    color: #333333;"></i>`;
            if (!e.originalEvent.fromElement ||
                e.originalEvent.fromElement.id !== "portfolio-item-hovered-icon"
            ) {
                $(e.target).after(icon);
            }
        },
        // handlerOut
        e => {
            if (e.toElement.id === "portfolio-item-hovered-icon") {
                $(e.target).css("filter", "opacity(.55)");
            }
            if (!e || e.toElement.id !== "portfolio-item-hovered-icon") {
                $(".fa-binoculars").remove();
                $(e.target).css("filter", "");
            }
        }
    );
    
    // remove the filter and the icon after clicked on a link
    $(".portfolio-link").click(e => {
        if (e.target.previousSibling.className === "img-responsive portfolio-item") {
            $(e.target.previousSibling).css("filter", "");
        }
        else {
            $(e.target).css("filter", "");
        }
        $(".fa-binoculars").remove();
    });

    // Add smooth scrolling to all links in navbar + back to top
    $(".navbar a, .back-to-top").on("click", function(event) {
        // Make sure this.hash has a value before overriding default behavior
        if (this.hash !== "") {
            // Prevent default anchor click behavior
            event.preventDefault();

            // Store hash
            let hash = this.hash;

            // Using jQuery's animate() method to add smooth page scroll
            $("html, body").animate({
                    scrollTop: $(hash).offset().top
                },
                // The optional number (900) specifies the number of milliseconds it takes to scroll to the specified area
                900,
                () => {
                    // Add hash (#) to URL when done scrolling (default click behavior)
                    window.location.hash = hash;
                }
            );
        }
    });
    
    $("#contact-form").submit(() => {
        let name = $("[name='name']").val();
        let email = $("[name='email']").val();
        let message = $("[name='message']").val();
        
        console.table({
            name,
            email,
            message
        });
        
    });

    $(window).scroll(function() {
        // set visible the navber when reach the about me section
        $("nav").toggleClass(
            "navbar-scrolled",
            $(this).scrollTop() > $("#about-me").position().top - 54
        );
        // play the contact form animation when it is in the viewport
        startContactAnimation();
        
        // animate back to top button
        if ($(this).scrollTop() > 200) {
            $(".back-to-top").fadeIn(900);
        } else {
            $(".back-to-top").fadeOut(300);
        }
        
        $(".slideanim").each(function() {
            var pos = $(this).offset().top;

            var winTop = $(window).scrollTop();
            if (pos < winTop + 600) {
                $(this).addClass("slide");
            }
        });
    });

    // Check if it's time to start the animation.
    var startContactAnimation = () => { 
        let formElement = $('#contact-form');

        
        if (formElement.hasClass('start-slide')) {
            // If the animation has finished and contact form's unvisible
            if(!isElementInViewport(formElement)) {
                formElement.removeClass("start-slide");
            }
            // If the animation has already been started
            return;  
        } 

        if (isElementInViewport(formElement)) {
            // Start the animation
            formElement.addClass('start-slide');
        }
    }
    
    var isElementInViewport = element => {
        let docViewTop = $(window).scrollTop();
        let docViewBottom = docViewTop + $(window).height();

        let elementTop = $(element).offset().top;
        let elementBottom = elementTop + $(element).height();

        return (elementTop <= docViewBottom) && (elementBottom >= docViewTop);
    };
});

  return (
    <nav class="navbar navbar-inverse bg-dark navbar-fixed-top">
      <div class="navbar-header">
        <button type="button" class="navbar-toggle" data-toggle="collapse" data-target=".navbar-collapse">
          <span class="icon-bar"></span>
          <span class="icon-bar"></span>
          <span class="icon-bar"></span>
        </button>
        <a href="#home" class="navbar-brand"><i class="fa fa-superpowers" aria-hidden="true"></i>MH</a>
      </div>
      <div class="collapse navbar-collapse">
        <ul class="nav navbar-nav navbar-right">
          <li class="active"><a href="#home"><span class="glyphicon glyphicon-home"></span> Home</a></li>
          <li><a href="#about-me">About me</a></li>
          <li><a href="#portfolio">Portfolio</a></li>
          <li><a href="#contact">Contact</a></li>
        </ul>
      </div>
    </nav>

  )
}
}

export default Navbar;

